<script type="text/javascript">
	$(document).ready(function(){
		$('#submit').click(function(){
		    var name1= $('#name').val();
			var email1= $('#email').val();
			var mobile1= $('#mobile').val();
             if(name1.length == "" || email1.length == "")
			{
				$("#name_err").html("name must be filled");
				$('#name_err').css("color","red");
				$("#email_err").html("email must be filled");
				$('#email_err').css("color","red");
	           $("#mobile_err").html("mobile number must be filled");
				$('#mobile_err').css("color","red");
			}

			else
			{

			$.ajax({
				url: "ajax.php",
				type: "post",
				data: {name:name1,
					email:email1,
					mobile:mobile1,

				},
				success: function(data,status){
					$('#thn01').html(data);
					$('#thn01').show();
                    $('#5').hide();
					$('#4').hide();
					$('#3').hide();
                    $('#2').hide();
					$('#1').hide();
			        $('#ths').hide();
				}
			});




		}

	});
		});
</script>



